				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$ \left\{ {\begin{array}{*{20}l}    {\begin{array}{*{20}c}    {{\text{Sn}},{\text{~~TPR}} = \frac{{{\text{TP}}}}{{{\text{TP}} + {\text{FN}}}}}  \\    {\begin{array}{*{20}c}    {{\text{Sp}},{\text{~~TNR}} = \frac{{{\text{TN}}}}{{{\text{TN}} + {\text{FP}}}}}  \\    {{\text{BACC}} = \frac{1}{2} \times \left( {{\text{TPR}} + {\text{TNR}}} \right)}  \\   \end{array} }  \\   \end{array} }  \\    {{\text{MCC}} = {\text{~}}\frac{{\left( {TP \times TN} \right) - \left( {FP \times FN} \right)}}{{\sqrt {\left( {{\text{TP}} + {\text{FP}}} \right) \times \left( {{\text{TP}} + {\text{FN}}} \right) \times \left( {{\text{TN}} + {\text{FP}}} \right) \times \left( {{\text{TN}} + {\text{FN}}} \right)} }}}  \\   \end{array} } \right. $$\end{document}Sn,TPR=TPTP+FNSp,TNR=TNTN+FPBACC=12×TPR+TNRMCC=TP×TN-FP×FNTP+FP×TP+FN×TN+FP×TN+FNwhere \documentclass[12pt]{minimal}
